import React, { useState } from "react";
import { Link } from "react-router-dom";
import { Heart } from "lucide-react";
import "../styles/PropertyCard.css";

export default function PropertyCard({ property }) {
  const [isFavorite, setIsFavorite] = useState(false);

  return (
    <div className="property-card group">
      {/* Image Container */}
      <div className="img-wrapper">
        {/* Wrap image in Link to details page */}
        <Link to={`/property/${property.id}`}>
          <img
            src={property.image}
            alt={property.title}
            className="property-img"
          />
        </Link>

        {/* RERA Approved Badge */}
        {property.reraApproved && <div className="rera-badge">RERA Approved</div>}

        {/* Favorite Button — stop propagation so clicking it doesn't navigate */}
        <button
          className="fav-btn"
          onClick={(e) => {
            e.preventDefault();
            e.stopPropagation();          // important: prevent Link navigation
            setIsFavorite(!isFavorite);
          }}
        >
          <Heart
            className={`heart-icon ${isFavorite ? "heart-active" : "heart-inactive"}`}
            strokeWidth={2}
          />
        </button>
      </div>

      {/* Info Section */}
      <div className="property-info">
        <h3>{property.title}</h3>
        <p className="loc">{property.location}</p>
        <p className="details">
          {property.type} • {property.sqft || "—"} Sqft • {property.price}
        </p>
      </div>
    </div>
  );
}
