import React, { useEffect, useState } from "react";
import "./styles/App.css";
import PropertyCard from "./components/PropertyCard";
import FloatingWhatsapp from "./components/FloatingWhatsapp";

function App() {
  const [properties, setProperties] = useState([]);

  // --------------------------- Fetch Properties --------------------------- //
  useEffect(() => {
    const fetchProperties = async () => {
      try {
        const res = await fetch("http://localhost:8080/api/properties");
        const data = await res.json();
        setProperties(data);
      } catch (err) {
        console.error("Failed to fetch properties", err);

        // fallback sample data
        setProperties([
          {
            id: 1,
            title: "Fallback Apartment",
            location: "Bengaluru",
            price: "‚Çπ50 Lakhs",
            image: "/images/prop1.jpg",
          },
        ]);
      }
    };

    fetchProperties();
  }, []);

  return (
    <div className="app-container">

      {/* --------------------------- Header --------------------------- */}
      <Header />

      {/* --------------------------- Main Content --------------------------- */}
      <main className="main-section">

        <SearchBar />

        <h2 className="section-title">Popular homes in Bengaluru</h2>

        <PropertyList properties={properties} />
      </main>

      <FloatingWhatsapp />
    </div>
  );
}

export default App;

// ============================================================================
// COMPONENTS
// ============================================================================

// --------------------------- Header Component --------------------------- //
function Header() {
  return (
    <header className="topbar">
      <div className="logo">
        <img
          src="/logo.png"
          alt="OGM Logo"
          className="logo-img"
        />
      </div>

      <button className="contact">Contact Us</button>
    </header>
  );
}

// --------------------------- Search Bar Component --------------------------- //
function SearchBar() {
  return (
    <div className="search-wrap">
      <label className="search-label">Where</label>

      <div className="search-input">
        <input
          placeholder="Find Apartments, Villas, Plots in Bengaluru"
        />
        <button className="search-btn">üîç</button>
      </div>
    </div>
  );
}

// --------------------------- Property List Component --------------------------- //
function PropertyList({ properties }) {
  if (!properties.length) {
    return <p style={{ marginTop: "20px" }}>No properties available.</p>;
  }

  return (
    <div className="property-grid">
      {properties.map((prop) => (
        <PropertyCard key={prop.id} property={prop} />
      ))}
    </div>
  );
}
